import { useEffect, useState } from "react"
import { mergeSort } from "./sort"

export function CreateCards(){
    const [highscore, setHighscore] = useState(0)
    const [clickedcards, setClickedCards] = useState([])
 
    const [score,setScore] = useState(0)
    let cardsdata = [
        {name:"netherlands",
        flag:"data:image/png;base64,",
        key: Math.random()      
    },
    {name:"france",
        flag:"data:image/png;base64,",
        key: Math.random()      
    },
    {name:"spain",
    flag:"data:image/png;base64,",
        key: Math.random()      
    },
    {name:"canada",
    flag:"data:image/png;base64,",
        key: Math.random()      
    },
    {name:"brazil",
    flag:"data:image/png;base64,",
        key: Math.random()      
    },
    {name:"argentina",
    flag:"data:image/png;base64,",
        key: Math.random()      
    },
    {name:"china",
        flag:"data:image/png;base64,",
        key: Math.random() 
    },
    {
        name:"australia",
        flag:"data:image/png;base64,",
        key: Math.random()
    },{
        name:"usa",
        flag:"data:image/png;base64,",
        key: Math.random()
    },
    {
        name:"germany",
        flag:"data:image/png;base64,",
        key: Math.random()
    }
    ]
    let sorteddata = mergeSort(cardsdata)
    useEffect(() => {
        if(score > highscore){
            setHighscore(score)
        }
    },[score])
    return (
        <>
        <div className="scores">
            <h1>score: {score}</h1>
            <h1>highscore: {highscore}</h1>
        </div>
        <div className="cards">
        {sorteddata.map((card) => (
            <div className="card" key={card.key} onClick={() => {
                
            let m = true   
            for(let i = 0;i < clickedcards.length; i++){
                console.log(i)
                if(clickedcards[i] == card.name || clickedcards.length == 0){
                    setScore(0)
                    setClickedCards([])
                    m = false
                }
            }
            if(m){
                setScore(score + 1)
                let x = clickedcards
                x.push(card.name)
                console.log(x)
                setClickedCards(x)
            }
            }}>
            <img src={card.flag} alt="" />
            <h3>{card.name}</h3>
            </div> 
            ))}
        </div>
    </>
)
}


